mapper.select:
select count(ID)
  from USERS
 where ID = #{id}

mapper.insert:

	select USERS_SEQ.nextval from dual

insert into USERS(
	NUM, ID, PW, F_NAME, S_NAME,EMAIL,IS_AUTHORIZED, IS_ADMIN, REG_DATE, LAST_LOGIN_DATE, LOGIN_IP
)
values (
	#{num}, #{id}, #{pw}, #{fName}, #{sName}, #{email}, #{isAuthorized, typeHandler=yesNoTypeHandler}, #{isAdmin, typeHandler=yesNoTypeHandler}, sysdate, sysdate, #{loginIP}
)
